mapper.select:
SELECT a.id, a.version, a.create_by createBy, nick_name nickName, a.create_time createTime, title, content, rich_text richText, type typeCode, dict_label typeName, (SELECT count(*) from px_leave_message where article_id = a.id) leaveMessageNumber FROM px_article a left join sys_user u on a.create_by = u.user_id left join sys_dict_data d on d.dict_value = a.type and d.dict_type = 'px_article_type' where 1 = 1
and type = #{type}
and a.id = #{articleId}
order by a.create_time desc

SELECT d.dict_value code, d.dict_label name, count( * ) number from px_article a LEFT JOIN sys_dict_data d ON a.type = d.dict_value AND d.dict_type = 'px_article_type'
where a.create_by = #{createBy}
GROUP BY d.dict_label, d.dict_value

select id, article_id articleId, author_header authorHeader, author_mailbox authorMailbox, author_name authorName, content, create_time createTime from px_leave_message where 1 = 1
and article_id = #{articleId}
and message_board = #{messageBoard}
